"3:165:13" : {s: 3, a: 165, w: 13, r: "عند", t: "عِندِ", l: "عِند", lc: 197, tl: "ʿindi", lb: "নিকটে",e: "from", b: "নিকট/কাছ থেকে", i: "sisi", f: 21, rm: "to go out of the right way, decline, deviate, be rebellious, tyrant, opposing, obstinate to resist, transgress the bounds.", ag: "أسم مجرور", eg: "Noun Genitive case", tag: "N", pos: "N", att: "GEN", qual: "", deg: "", gen: "", num: "", mood: "", lu: ""},
