window.translation = $.extend(window.translation || {}, {
"15:32" : {s: 15, a: 32, e: "God said, &lsquo;Iblis, why did you not bow down like the others?&rsquo;", b: "আল্লাহ বললেনঃ হে ইবলিস, তোমার কি হলো যে তুমি সেজদাকারীদের অন্তর্ভূক্ত হতে স্বীকৃত হলে না?"},
"15:33" : {s: 15, a: 33, e: "and he answered, &lsquo;I will not bow to a mortal You created from dried clay, formed from dark mud.&rsquo;", b: "বললঃ আমি এমন নই যে, একজন মানবকে সেজদা করব, যাকে আপনি পচা কর্দম থেকে তৈরী ঠনঠনে বিশুষ্ক মাটি দ্বারা সৃষ্টি করেছেন।"},
"15:34" : {s: 15, a: 34, e: "&lsquo;Get out of here!&rsquo; said God. &lsquo;You are an outcast,", b: "আল্লাহ বললেনঃ তবে তুমি এখান থেকে বের হয়ে যাও। তুমি বিতাড়িত।"},
"15:35" : {s: 15, a: 35, e: "rejected until the Day of Judgement.&rsquo;", b: "এবং তোমার প্রতি ন্যায় বিচারের দিন পর্যন্ত অভিসম্পাত।"},
"15:36" : {s: 15, a: 36, e: "Iblis said, &lsquo;My Lord, give me respite until the Day when they are raised from the dead.&rsquo;", b: "সে বললঃ হে আমার পালনকর্তা, আপনি আমাকে পুনরুত্থান দিবস পর্যন্ত অবকাশ দিন।"},
"15:37" : {s: 15, a: 37, e: "&lsquo;You have respite,&rsquo; said God,", b: "আল্লাহ বললেনঃ তোমাকে অবকাশ দেয়া হল।"},
"15:38" : {s: 15, a: 38, e: "&lsquo;until the Day of the Appointed Time.&rsquo;", b: "সেই অবধারিত সময় উপস্থিত হওয়ার দিন পর্যন্ত।"},
"15:39" : {s: 15, a: 39, e: "Iblis then said to God, &lsquo;Because You have put me in the wrong, I will lure mankind on earth and put them in the wrong,", b: "সে বললঃ হে আমার পলনকর্তা, আপনি যেমন আমাকে পথ ভ্রষ্ট করেছেন, আমিও তাদের সবাইকে পৃথিবীতে নানা সৌন্দর্যে আকৃষ্ট করব এবং তাদের সবাইকে পথ ভ্রষ্ঠ করে দেব।"},
"15:40" : {s: 15, a: 40, e: "all except Your devoted servants.&rsquo;", b: "আপনার মনোনীত বান্দাদের ব্যতীত।"},
"15:41" : {s: 15, a: 41, e: "God said, &lsquo;[Devotion] is a straight path to Me:", b: "আল্লাহ বললেনঃ এটা আমা পর্যন্ত সোজা পথ।"},
"15:42" : {s: 15, a: 42, e: "you will have no power over My servants, only over the ones who go astray and follow you.", b: "যারা আমার বান্দা, তাদের উপর তোমার কোন ক্ষমতা নেই; কিন্তু পথভ্রান্তদের মধ্য থেকে যারা তোমার পথে চলে।"},
"15:43" : {s: 15, a: 43, e: "Hell is the promised place for all these,", b: "তাদের সবার নির্ধারিত স্থান হচ্ছে জাহান্নাম।"},
"15:44" : {s: 15, a: 44, e: "with seven gates, each gate having its allotted share of them.", b: "এর সাতটি দরজা আছে। প্রত্যেক দরজার জন্যে এক একটি পৃথক দল আছে।"},
"15:45" : {s: 15, a: 45, e: "But the righteous will be in Gardens with springs-", b: "নিশ্চয় খোদাভীরুরা বাগান ও নির্ঝরিনীসহূহে থাকবে।"},
"15:46" : {s: 15, a: 46, e: "&ldquo;Enter them in peace and safety!&rdquo;&ndash;&ndash;", b: "বলা হবেঃ এগুলোতে নিরাপত্তা ও শান্তি সহকরে প্রবেশ কর।"},
"15:47" : {s: 15, a: 47, e: "and We shall remove any bitterness from their hearts: [they will be like] brothers, sitting on couches, face to face.", b: "তাদের অন্তরে যে ক্রোধ ছিল, আমি তা দূর করে দেব। তারা ভাই ভাইয়ের মত সামনা-সামনি আসনে বসবে।"},
"15:48" : {s: 15, a: 48, e: "No weariness will ever touch them there, nor will they ever be expelled.&rsquo;", b: "সেখানে তাদের মোটেই কষ্ট হবে না এবং তারা সেখান থেকে বহিস্কৃত হবে না।"},
"15:49" : {s: 15, a: 49, e: "[Prophet], tell My servants that I am the Forgiving, the Merciful,", b: "আপনি আমার বান্দাদেরকে জানিয়ে দিন যে, আমি অত্যন্ত ক্ষমাশীল দয়ালু।"},
"15:50" : {s: 15, a: 50, e: "but My torment is the truly painful one.", b: "এবং ইহাও যে, আমার শাস্তিই যন্ত্রনাদায়ক শাস্তি।"},
"15:51" : {s: 15, a: 51, e: "Tell them too about Abraham&rsquo;s guests:", b: "আপনি তাদেরকে ইব্রাহীমের মেহমানদের অবস্থা শুনিয়ে দিন।"},
});
