window.translation = $.extend(window.translation || {}, {
"15:71" : {s: 15, a: 71, e: "He said, &lsquo;My daughters are here, if you must.&rsquo;", b: "তিনি বললেনঃ যদি তোমরা একান্ত কিছু করতেই চাও, তবে আমার কন্যারা উপস্থিত আছে।"},
"15:72" : {s: 15, a: 72, e: "By your life [Prophet], they wandered on in their wild intoxication", b: "আপনার প্রাণের কসম, তারা আপন নেশায় প্রমত্ত ছিল।"},
"15:73" : {s: 15, a: 73, e: "and the blast overtook them at sunrise:", b: "অতঃপর সুর্যোদয়ের সময় তাদেরকে প্রচন্ড একটি শব্দ এসে পাকড়াও করল।"},
"15:74" : {s: 15, a: 74, e: "We turned their city upside down and rained on them a shower of clay stones.", b: "অতঃপর আমি জনপদটিকে উল্টে দিলাম এবং তাদের উপর কঙ্করের প্রস্থর বর্ষণ করলাম।"},
"15:75" : {s: 15, a: 75, e: "There truly is a sign in this for those who can learn-", b: "নিশ্চয় এতে চিন্তাশীলদের জন্যে নিদর্শনাবলী রয়েছে।"},
"15:76" : {s: 15, a: 76, e: "it is still there on the highway-", b: "জনপদটি সোজা পথে অবস্থিত রয়েছে।"},
"15:77" : {s: 15, a: 77, e: "there truly is a sign in this for those who believe.", b: "নিশ্চয় এতে ঈমানদারদের জন্যে নিদর্শণ আছে।"},
"15:78" : {s: 15, a: 78, e: "The forest-dwellers, too, were wrongdoers", b: "নিশ্চয় গহীন বনের অধিবাসীরা পাপী ছিল।"},
"15:79" : {s: 15, a: 79, e: "and We took retribution on them; both are still there on the highway, plain for all to see.", b: "অতঃপর আমি তাদের কাছ থেকে প্রতিশোধ নিয়েছি। উভয় বস্তি প্রকাশ্য রাস্তার উপর অবস্থিত।"},
"15:80" : {s: 15, a: 80, e: "The people of al-Hijr also rejected Our messengers:", b: "নিশ্চয় হিজরের বাসিন্দারা পয়গম্বরগণের প্রতি মিথ্যারোপ করেছে।"},
"15:81" : {s: 15, a: 81, e: "We gave them Our signs, but they turned their backs.", b: "আমি তাদেরকে নিজের নিদর্শনাবলী দিয়েছি। অতঃপর তারা এগুলো থেকে মুখ ফিরিয়ে নেয়।"},
"15:82" : {s: 15, a: 82, e: "They carved out dwellings in the mountains, and lived in security-", b: "তারা পাহাড়ে নিশ্চিন্তে ঘর খোদাই করত।"},
"15:83" : {s: 15, a: 83, e: "the blast overwhelmed them early in the morning.", b: "অতঃপর এক প্রত্যুষে তাদের উপর একটা শব্দ এসে আঘাত করল।"},
"15:84" : {s: 15, a: 84, e: "What they had gained was of no use to them.", b: "তখন কোন উপকারে আসল না যা তারা উপার্জন করেছিল।"},
"15:85" : {s: 15, a: 85, e: "We did not create the heavens and the earth and everything between them without a true purpose: the Hour will certainly come, so [Prophet] bear with them graciously.", b: "আমি নভোমন্ডল, ভুমন্ডল এবং এতদুভয়ের মধ্যবর্তী যা আছে তা তাৎপর্যহীন সৃষ্টি করিনি। কেয়ামত অবশ্যই আসবে। অতএব পরম ঔদাসীন্যের সাথে ওদের ক্রিয়াকর্ম উপক্ষো করুন।"},
"15:86" : {s: 15, a: 86, e: "Your Lord is the All Knowing Creator.", b: "নিশ্চয় আপনার পালনকর্তাই স্রষ্টা, সর্বজ্ঞ।"},
"15:87" : {s: 15, a: 87, e: "We have given you the seven oft-recited versesand the whole glorious Quran.", b: "আমি আপনাকে সাতটি বার বার পঠিতব্য আয়াত এবং মহান কোরআন দিয়েছি।"},
"15:88" : {s: 15, a: 88, e: "Do not look longingly at the good things We have given some to enjoy. Do not grieve over the [disbelievers], but lower your wings over the believers", b: "আপনি চক্ষু তুলে ঐ বস্তুর প্রতি দেখবেন না, যা আমি তাদের মধ্যে কয়েক প্রকার লোককে ভোগ করার জন্যে দিয়েছি, তাদের জন্যে চিন্তিত হবেন না আর ঈমানদারদের জন্যে স্বীয় বাহু নত করুন।"},
"15:89" : {s: 15, a: 89, e: "and say, &lsquo;I am here to give plain warning,&rsquo;", b: "আর বলুনঃ আমি প্রকাশ্য ভয় প্রদর্শক।"},
"15:90" : {s: 15, a: 90, e: "like the [warning] We have sent down for those who divide themselves into bands", b: "যেমন আমি নাযিল করেছি যারা বিভিন্ন মতে বিভক্ত তাদের উপর।"},
});
