window.translation = $.extend(window.translation || {}, {
"19:1" : {s: 19, a: 1, e: "Kaf Ha Ya 'Ayn Sad", b: "কাফ-হা-ইয়া-আইন-সাদ", t: "بِسْمِ ٱللَّهِ ٱلرَّحْمَٰنِ ٱلرَّحِيمِ كٓهيعٓصٓ"},
"19:2" : {s: 19, a: 2, e: "This is an account of your Lord&rsquo;s grace towards His servant, Zachariah,", b: "এটা আপনার পালনকর্তার অনুগ্রহের বিবরণ তাঁর বান্দা যাকারিয়ার প্রতি।", t: "ذِكْرُ رَحْمَتِ رَبِّكَ عَبْدَهُۥ زَكَرِيَّآ"},
"19:3" : {s: 19, a: 3, e: "when he called to his Lord secretly, saying,", b: "যখন সে তাঁর পালনকর্তাকে আহবান করেছিল নিভৃতে।", t: "إِذْ نَادَىٰ رَبَّهُۥ نِدَآءً خَفِيًّا"},
"19:4" : {s: 19, a: 4, e: "&lsquo;Lord, my bones have weakened and my hair is ashen grey, but never, Lord, have I ever prayed to You in vain:", b: "সে বললঃ হে আমার পালনকর্তা আমার অস্থি বয়স-ভারাবনত হয়েছে; বার্ধক্যে মস্তক সুশুভ্র হয়েছে; হে আমার পালনকর্তা! আপনাকে ডেকে আমি কখনও বিফলমনোরথ হইনি।", t: "قَالَ رَبِّ إِنِّى وَهَنَ ٱلْعَظْمُ مِنِّى وَٱشْتَعَلَ ٱلرَّأْسُ شَيْبًا وَلَمْ أَكُنۢ بِدُعَآئِكَ رَبِّ شَقِيًّا"},
"19:5" : {s: 19, a: 5, e: "I fear [what] my kinsmen [will do] when I am gone, for my wife is barren, so grant me a successor&ndash;&ndash;a gift from You&ndash;&ndash;", b: "আমি ভয় করি আমার পর আমার স্বগোত্রকে এবং  আমার স্ত্রী বন্ধ্যা; কাজেই আপনি নিজের পক্ষ থেকে আমাকে এক জন কর্তব্য পালনকারী দান করুন।", t: "وَإِنِّى خِفْتُ ٱلْمَوَٰلِىَ مِن وَرَآءِى وَكَانَتِ ٱمْرَأَتِى عَاقِرًا فَهَبْ لِى مِن لَّدُنكَ وَلِيًّا"},
"19:6" : {s: 19, a: 6, e: "to be my heir and the heir of the family of Jacob. Lord, make him well pleasing [to You].&rsquo;", b: "সে আমার স্থলাভিষিক্ত হবে ইয়াকুব বংশের এবং হে আমার পালনকর্তা, তাকে করুন সন্তোষজনক।", t: "يَرِثُنِى وَيَرِثُ مِنْ ءَالِ يَعْقُوبَ ۖ وَٱجْعَلْهُ رَبِّ رَضِيًّا"},
"19:7" : {s: 19, a: 7, e: "&lsquo;Zachariah, We bring you good news of a son whose name will be John&ndash;&ndash;We have chosen this name for no one before him.&rsquo;", b: "হে যাকারিয়া, আমি তোমাকে এক পুত্রের সুসংবাদ দিচ্ছি, তার নাম হবে ইয়াহইয়া। ইতিপূর্বে এই নামে আমি কারও নাম করণ করিনি।", t: "يَٰزَكَرِيَّآ إِنَّا نُبَشِّرُكَ بِغُلَٰمٍ ٱسْمُهُۥ يَحْيَىٰ لَمْ نَجْعَل لَّهُۥ مِن قَبْلُ سَمِيًّا"},
"19:8" : {s: 19, a: 8, e: "He said, &lsquo;Lord, how can I have a son when my wife is barren, and I am old and frail?&rsquo;", b: "সে বললঃ হে আমার পালনকর্তা কেমন করে আমার পুত্র হবে অথচ আমার স্ত্রী যে বন্ধ্যা, আর আমিও যে বার্ধক্যের শেষ প্রান্তে উপনীত।", t: "قَالَ رَبِّ أَنَّىٰ يَكُونُ لِى غُلَٰمٌ وَكَانَتِ ٱمْرَأَتِى عَاقِرًا وَقَدْ بَلَغْتُ مِنَ ٱلْكِبَرِ عِتِيًّا"},
"19:9" : {s: 19, a: 9, e: "He said, &lsquo;This is what your Lord has said: &ldquo;It is easy for Me: I created you, though you were nothing before.&rdquo; &rsquo;", b: "তিনি বললেনঃ এমনিতেই হবে। তোমার পালনকর্তা বলে দিয়েছেনঃ এটা আমার পক্ষে সহজ। আমি তো পুর্বে তোমাকে সৃষ্টি করেছি এবং তুমি কিছুই ছিলে না।", t: "قَالَ كَذَٰلِكَ قَالَ رَبُّكَ هُوَ عَلَىَّ هَيِّنٌ وَقَدْ خَلَقْتُكَ مِن قَبْلُ وَلَمْ تَكُ شَيْـًٔا"},
"19:10" : {s: 19, a: 10, e: "He said, &lsquo;Give me a sign, Lord.&rsquo; He said, &lsquo;Your sign is that you will not [be able to] speak to anyone for three full [days and] nights.&rsquo;", b: "সে বললঃ হে আমার পালনকর্তা, আমাকে একটি নির্দশন দিন। তিনি বললেন তোমার নিদর্শন এই যে, তুমি সুস্থ অবস্থায় তিন দিন মানুষের সাথে কথাবার্তা বলবে না।", t: "قَالَ رَبِّ ٱجْعَل لِّىٓ ءَايَةً ۚ قَالَ ءَايَتُكَ أَلَّا تُكَلِّمَ ٱلنَّاسَ ثَلَٰثَ لَيَالٍ سَوِيًّا"},
"19:11" : {s: 19, a: 11, e: "He went out of the sanctuary to his people and signalled to them to praise God morning and evening.", b: "অতঃপর সে কক্ষ থেকে বের হয়ে তার সম্প্রদায়ের কাছে এল এবং ইঙ্গিতে তাদেরকে সকাল সন্ধ্যায় আল্লাহকে স্মরণ করতে বললঃ", t: "فَخَرَجَ عَلَىٰ قَوْمِهِۦ مِنَ ٱلْمِحْرَابِ فَأَوْحَىٰٓ إِلَيْهِمْ أَن سَبِّحُوا۟ بُكْرَةً وَعَشِيًّا"},
});
