window.translation = $.extend(window.translation || {}, {
"19:96" : {s: 19, a: 96, e: "But the Lord of Mercy will give love to those who believe and do righteous deeds:", b: "যারা বিশ্বাস স্থাপন করে এবং সৎকর্ম সম্পাদন করে, তাদেরকে দয়াময় আল্লাহ ভালবাসা দেবেন।", t: "إِنَّ ٱلَّذِينَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّٰلِحَٰتِ سَيَجْعَلُ لَهُمُ ٱلرَّحْمَٰنُ وُدًّا"},
"19:97" : {s: 19, a: 97, e: "We have made it easy, in your own language [Prophet], so that you may bring glad news to the righteous and warnings to a stubborn people.", b: "আমি কোরআনকে আপনার ভাষায় সহজ করে দিয়েছি, যাতে আপনি এর দ্বারা পরহেযগারদেরকে সুসংবাদ দেন এবং কলহকারী সম্প্রদায়কে সতর্ক করেন।", t: "فَإِنَّمَا يَسَّرْنَٰهُ بِلِسَانِكَ لِتُبَشِّرَ بِهِ ٱلْمُتَّقِينَ وَتُنذِرَ بِهِۦ قَوْمًا لُّدًّا"},
"19:98" : {s: 19, a: 98, e: "How many generations We have destroyed before them! Do you perceive a single one of them now, or hear as much as a whisper?", b: "তাদের পূর্বে আমি কত মানবগোষ্ঠীকে ধ্বংস করেছি। আপনি কি তাদের কাহারও সাড়া পান, অথবা তাদের ক্ষীনতম আওয়ায ও শুনতে পান?", t: "وَكَمْ أَهْلَكْنَا قَبْلَهُم مِّن قَرْنٍ هَلْ تُحِسُّ مِنْهُم مِّنْ أَحَدٍ أَوْ تَسْمَعُ لَهُمْ رِكْزًۢا"},
"20:1" : {s: 20, a: 1, e: "Ta Ha", b: "তোয়া-হা", t: "بِسْمِ ٱللَّهِ ٱلرَّحْمَٰنِ ٱلرَّحِيمِ طه"},
"20:2" : {s: 20, a: 2, e: "It was not to distress you [Prophet] that We sent down the Quran to you,", b: "আপনাকে ক্লেশ দেবার জন্য আমি আপনার প্রতি কোরআন অবতীর্ণ করিনি।", t: "مَآ أَنزَلْنَا عَلَيْكَ ٱلْقُرْءَانَ لِتَشْقَىٰٓ"},
"20:3" : {s: 20, a: 3, e: "but as a reminder for those who hold God in awe,", b: "কিন্তু তাদেরই উপদেশের জন্য যারা ভয় করে।", t: "إِلَّا تَذْكِرَةً لِّمَن يَخْشَىٰ"},
"20:4" : {s: 20, a: 4, e: "a revelation from the One who created the earth and the high heaven,", b: "এটা তাঁর কাছ থেকে অবতীর্ণ, যিনি ভূমন্ডল ও সমুচ্চ নভোমন্ডল সৃষ্টি করেছেন।", t: "تَنزِيلًا مِّمَّنْ خَلَقَ ٱلْأَرْضَ وَٱلسَّمَٰوَٰتِ ٱلْعُلَى"},
"20:5" : {s: 20, a: 5, e: "the Lord of Mercy, established on the throne.", b: "তিনি পরম দয়াময়, আরশে সমাসীন হয়েছেন।", t: "ٱلرَّحْمَٰنُ عَلَى ٱلْعَرْشِ ٱسْتَوَىٰ"},
"20:6" : {s: 20, a: 6, e: "Everything in the heavens and on earth, everything between them, everything beneath the soil, belongs to Him.", b: "নভোমন্ডলে, ভুমন্ডলে, এতদুভয়ের মধ্যবর্তী স্থানে এবং সিক্ত ভূগর্ভে যা আছে, তা তাঁরই।", t: "لَهُۥ مَا فِى ٱلسَّمَٰوَٰتِ وَمَا فِى ٱلْأَرْضِ وَمَا بَيْنَهُمَا وَمَا تَحْتَ ٱلثَّرَىٰ"},
"20:7" : {s: 20, a: 7, e: "Whatever you may say aloud, He knows what you keep secret and what is even more hidden.", b: "যদি তুমি উচ্চকন্ঠেও কথা বল, তিনি তো গুপ্ত ও তদপেক্ষাও গুপ্ত বিষয়বস্তু জানেন।", t: "وَإِن تَجْهَرْ بِٱلْقَوْلِ فَإِنَّهُۥ يَعْلَمُ ٱلسِّرَّ وَأَخْفَى"},
"20:8" : {s: 20, a: 8, e: "God&ndash;&ndash;there is no god but Him&ndash;&ndash;the most excellent names belong to Him.", b: "আল্লাহ তিনি ব্যতীত কোন উপাস্য ইলাহ নেই। সব সৌন্দর্যমন্ডিত নাম তাঁরই।", t: "ٱللَّهُ لَآ إِلَٰهَ إِلَّا هُوَ ۖ لَهُ ٱلْأَسْمَآءُ ٱلْحُسْنَىٰ"},
"20:9" : {s: 20, a: 9, e: "Has the story of Moses come to you [Prophet]?", b: "আপনার কাছে মূসার বৃত্তান্ত পৌঁছেছে কি।", t: "وَهَلْ أَتَىٰكَ حَدِيثُ مُوسَىٰٓ"},
"20:10" : {s: 20, a: 10, e: "He saw a fire and said to his people, &lsquo;Stay here&ndash;&ndash;I can see a fire. Maybe I can bring you a flaming brand from it or find some guidance there.&rsquo;", b: "তিনি যখন আগুন দেখলেন, তখন পরিবারবর্গকে বললেনঃ তোমরা এখানে অবস্থান কর আমি আগুন দেখেছি। সম্ভবতঃ আমি তা থেকে তোমাদের কাছে কিছু আগুন জালিয়ে আনতে পারব অথবা আগুনে পৌছে পথের সন্ধান পাব।", t: "إِذْ رَءَا نَارًا فَقَالَ لِأَهْلِهِ ٱمْكُثُوٓا۟ إِنِّىٓ ءَانَسْتُ نَارًا لَّعَلِّىٓ ءَاتِيكُم مِّنْهَا بِقَبَسٍ أَوْ أَجِدُ عَلَى ٱلنَّارِ هُدًى"},
"20:11" : {s: 20, a: 11, e: "When he came to the fire, he was summoned, &lsquo;Moses!", b: "অতঃপর যখন তিনি আগুনের কাছে পৌছলেন, তখন আওয়াজ আসল হে মূসা,", t: "فَلَمَّآ أَتَىٰهَا نُودِىَ يَٰمُوسَىٰٓ"},
"20:12" : {s: 20, a: 12, e: "I am your Lord. Take off your shoes: you are in the sacred valley of Tuwa.", b: "আমিই তোমার পালনকর্তা, অতএব তুমি জুতা খুলে ফেল, তুমি পবিত্র উপত্যকা তুয়ায় রয়েছ।", t: "إِنِّىٓ أَنَا۠ رَبُّكَ فَٱخْلَعْ نَعْلَيْكَ ۖ إِنَّكَ بِٱلْوَادِ ٱلْمُقَدَّسِ طُوًى"},
});
