window.translation = $.extend(window.translation || {}, {
"26:112" : {s: 26, a: 112, e: "He said, &lsquo;What knowledge do I have of what they used to do?", b: "নূহ বললেন, তারা কি কাজ করছে, তা জানা আমার কি দরকার?"},
"26:113" : {s: 26, a: 113, e: "It is for my Lord alone to bring them to account- if only you could see-", b: "তাদের হিসাব নেয়া আমার পালনকর্তারই কাজ; যদি তোমরা বুঝতে!"},
"26:114" : {s: 26, a: 114, e: "I will not drive believers away.", b: "আমি মুমিনগণকে তাড়িয়ে দেয়ার লোক নই।"},
"26:115" : {s: 26, a: 115, e: "I am here only to give people a clear warning.&rsquo;", b: "আমি তো শুধু একজন সুস্পষ্ট সতর্ককারী।"},
"26:116" : {s: 26, a: 116, e: "So they said, &lsquo;Noah, if you do not stop this, you will be stoned.&rsquo;", b: "তারা বলল, হে নূহ যদি তুমি বিরত না হও, তবে তুমি নিশ্চিতই প্রস্তরাঘাতে নিহত হবে।"},
"26:117" : {s: 26, a: 117, e: "He said, &lsquo;My Lord, my people have rejected me,", b: "নূহ বললেন, হে আমার পালনকর্তা, আমার সম্প্রদায় তো আমাকে মিথ্যাবাদী বলছে।"},
"26:118" : {s: 26, a: 118, e: "so make a firm judgement between me and them, and save me and my believing followers.&rsquo;", b: "অতএব, আমার ও তাদের মধ্যে কোন ফয়সালা করে দিন এবং আমাকে ও আমার সংগী মুমিনগণকে রক্ষা করুন।"},
"26:119" : {s: 26, a: 119, e: "So We saved him and his followers in the fully laden ship,", b: "অতঃপর আমি তাঁকে ও তাঁর সঙ্গিগণকে বোঝাই করা নৌকায় রক্ষা করলাম।"},
"26:120" : {s: 26, a: 120, e: "and drowned the rest.", b: "এরপর অবশিষ্ট সবাইকে নিমজ্জত করলাম।"},
"26:121" : {s: 26, a: 121, e: "There truly is a sign in this, though most of them do not believe:", b: "নিশ্চয় এতে নিদর্শন আছে এবং তাদের অধিকাংশই বিশ্বাসী নয়।"},
"26:122" : {s: 26, a: 122, e: "your Lord alone is the Almighty, the Merciful.", b: "নিশ্চয় আপনার পালনকর্তা প্রবল পরাক্রমশালী, পরম দয়ালু।"},
"26:123" : {s: 26, a: 123, e: "The people of Ad, too, called the messengers liars.", b: "আদ সম্প্রদায় পয়গম্বরগণকে মিথ্যাবাদী বলেছে।"},
"26:124" : {s: 26, a: 124, e: "Their brother Hud said to them, &lsquo;Will you not be mindful of God?", b: "তখন তাদের ভাই হুদ তাদেরকে বললেনঃ তোমাদের কি ভয় নেই?"},
"26:125" : {s: 26, a: 125, e: "I am a faithful messenger sent to you:", b: "আমি তোমাদের বিশ্বস্ত রসূল।"},
"26:126" : {s: 26, a: 126, e: "be mindful of God and obey me.", b: "অতএব, তোমরা আল্লাহকে ভয় কর এবং আমার আনুগত্য কর।"},
"26:127" : {s: 26, a: 127, e: "I ask no reward of you, for my only reward is with the Lord of the Worlds.", b: "আমি তোমাদের কাছে এর জন্যে প্রতিদান চাই না। আমার প্রতিদান তো পালনকর্তা দেবেন।"},
"26:128" : {s: 26, a: 128, e: "How can you be so vain that you set up monuments on every high place?", b: "তোমরা কি প্রতিটি উচ্চস্থানে অযথা নিদর্শন নির্মান করছ?"},
"26:129" : {s: 26, a: 129, e: "Do you build fortresses because you hope to be immortal?", b: "এবং বড় বড় প্রাসাদ নির্মাণ করছ, যেন তোমরা চিরকাল থাকবে?"},
"26:130" : {s: 26, a: 130, e: "Why do you act like tyrants whenever you attack someone?", b: "যখন তোমরা আঘাত হান, তখন জালেম ও নিষ্ঠুরের মত আঘাত হান।"},
"26:131" : {s: 26, a: 131, e: "Be mindful of God and obey me;", b: "অতএব, আল্লাহকে ভয় কর এবং আমার অনুগত্য কর।"},
"26:132" : {s: 26, a: 132, e: "be mindful of Him who has provided you with everything you know-", b: "ভয় কর তাঁকে, যিনি তোমাদেরকে সেসব বস্তু দিয়েছেন, যা তোমরা জান।"},
"26:133" : {s: 26, a: 133, e: "He has given you livestock, sons,", b: "তোমাদেরকে দিয়েছেন চতুষ্পদ জন্তু ও পুত্র-সন্তান,"},
"26:134" : {s: 26, a: 134, e: "gardens, springs-", b: "এবং উদ্যান ও ঝরণা।"},
"26:135" : {s: 26, a: 135, e: "for I truly fear that the torment of a grievous day will overtake you.&rsquo;", b: "আমি তোমাদের জন্যে মহাদিবসের শাস্তি আশংকা করি।"},
"26:136" : {s: 26, a: 136, e: "They replied, &lsquo;It makes no difference to us whether you warn us or not,", b: "তারা বলল, তুমি উপদেশ দাও অথবা উপদেশ নাই দাও, উভয়ই আমাদের জন্যে সমান।"},
});
