"36:63:2" : {s: 36, a: 63, w: 2, r: "", t: "جَهَنَّمُ", l: "جَهَنَّم", lc: 77, tl: "jahannamu", lb: "জাহান্নাম",e: "(the) Hell", b: "জাহান্নাম", i: "Jahannam", f: 18, rm: ""},
"36:63:3" : {s: 36, a: 63, w: 3, r: "", t: "ٱلَّتِى", l: "ٱلَّذِى", lc: 1464, tl: "allatī", lb: "যে",e: "which", b: "যা (এমন যে)", i: "adalah", f: 57, rm: ""},
"36:63:4" : {s: 36, a: 63, w: 4, r: "كون", t: "كُنتُمْ", l: "كَانَ", lc: 1358, tl: "kuntum", lb: "হয়, হতো",e: "you were", b: "তোমরা হও", i: "kamu", f: 152, rm: "To be, exist, happen, occur, take place, become, be such or so, originated."},
"36:63:5" : {s: 36, a: 63, w: 5, r: "وعد", t: "تُوعَدُونَ", l: "وَعَدَ", lc: 70, tl: "tūʿadūna", lb: "শপথ, কথা দেওয়া",e: "promised", b: "ওয়াদা করা হয়েছে", i: "kamu diancam", f: 12, rm: "to promise, give ones word, threaten, promise good, threaten (depending on context)."},
"36:64:1" : {s: 36, a: 64, w: 1, r: "صلي", t: "ٱصْلَوْهَا", l: "يَصْلَى", lc: 13, tl: "iṣ'lawhā", lb: "প্রবেশ করা",e: "Burn therein", b: "তোমরা তাতে প্রবেশ কর", i: "masuklah ke dalamnya", f: 2, rm: "to warm at the fire, endure heat, to roast/fry/broil/burn."},
