"36:77:8" : {s: 36, a: 77, w: 8, r: "", t: "فَإِذَا", l: "إِذَا", lc: 409, tl: "fa-idhā", lb: "যখন",e: "Then behold", b: "এরপর যখন", i: "maka tiba-tiba", f: 86, rm: ""},
