window.translation = $.extend(window.translation || {}, {
"37:77" : {s: 37, a: 77, e: "We let his offspring remain on the earth,", b: "এবং তার বংশধরদেরকেই আমি অবশিষ্ট রেখেছিলাম।"},
"37:78" : {s: 37, a: 78, e: "We let him be praised by later generations:", b: "আমি তার জন্যে পরবর্তীদের মধ্যে এ বিষয় রেখে দিয়েছি যে,"},
"37:79" : {s: 37, a: 79, e: "&lsquo;Peace be upon Noah among all the nations!&rsquo;", b: "বিশ্ববাসীর মধ্যে নূহের প্রতি শান্তি বর্ষিত হোক।"},
"37:80" : {s: 37, a: 80, e: "This is how We reward those who do good:", b: "আমি এভাবেই সৎকর্ম পরায়নদেরকে পুরস্কৃত করে থাকি।"},
"37:81" : {s: 37, a: 81, e: "he was truly one of Our faithful servants.", b: "সে ছিল আমার ঈমানদার বান্দাদের অন্যতম।"},
"37:82" : {s: 37, a: 82, e: "We drowned the rest.", b: "অতঃপর আমি অপরাপর সবাইকে নিমজ্জত করেছিলাম।"},
"37:83" : {s: 37, a: 83, e: "Abraham was of the same faith:", b: "আর নূহ পন্থীদেরই একজন ছিল ইব্রাহীম।"},
"37:84" : {s: 37, a: 84, e: "he came to his Lord with a devoted heart.", b: "যখন সে তার পালনকর্তার নিকট সুষ্ঠু চিত্তে উপস্থিত হয়েছিল,"},
"37:85" : {s: 37, a: 85, e: "He said to his father and his people, &lsquo;What are you worshipping?", b: "যখন সে তার পিতা ও সম্প্রদায়কে বলেছিলঃ তোমরা কিসের উপাসনা করছ?"},
"37:86" : {s: 37, a: 86, e: "How can you choose false gods instead of the true God?", b: "তোমরা কি আল্লাহ ব্যতীত মিথ্যা উপাস্য কামনা করছ?"},
"37:87" : {s: 37, a: 87, e: "So what is your opinion about the Lord of all the Worlds?&rsquo;", b: "বিশ্বজগতের পালনকর্তা সম্পর্কে তোমাদের ধারণা কি?"},
"37:88" : {s: 37, a: 88, e: "then he looked up to the stars.", b: "অতঃপর সে একবার তারকাদের প্রতি লক্ষ্য করল।"},
"37:89" : {s: 37, a: 89, e: "He said, &lsquo;I am sick,&rsquo;", b: "এবং বললঃ আমি পীড়িত।"},
"37:90" : {s: 37, a: 90, e: "so [his people] turned away from him and left.", b: "অতঃপর তারা তার প্রতি পিঠ ফিরিয়ে চলে গেল।"},
"37:91" : {s: 37, a: 91, e: "He turned to their gods and said,", b: "অতঃপর সে তাদের দেবালয়ে, গিয়ে ঢুকল এবং বললঃ তোমরা খাচ্ছ না কেন?"},
"37:92" : {s: 37, a: 92, e: "&lsquo;Do you not eat? Why do you not speak?&rsquo;", b: "তোমাদের কি হল যে, কথা বলছ না?"},
"37:93" : {s: 37, a: 93, e: "then he turned and struck them with his right arm.", b: "অতঃপর সে প্রবল আঘাতে তাদের উপর ঝাঁপিয়ে পড়ল।"},
"37:94" : {s: 37, a: 94, e: "His people hurried towards him,", b: "তখন লোকজন তার দিকে ছুটে এলো ভীত-সন্ত্রস্ত পদে।"},
"37:95" : {s: 37, a: 95, e: "but he said, &lsquo;How can you worship things you carve with your own hands,", b: "সে বললঃ তোমরা স্বহস্ত নির্মিত পাথরের পূজা কর কেন?"},
"37:96" : {s: 37, a: 96, e: "when it is God who has created you and all your handiwork?&rsquo;", b: "অথচ আল্লাহ তোমাদেরকে এবং তোমরা যা নির্মাণ করছ সবাইকে সৃষ্টি করেছেন।"},
"37:97" : {s: 37, a: 97, e: "They said, &lsquo;Build a pyre and throw him into the blazing fire.&rsquo;", b: "তারা বললঃ এর জন্যে একটি ভিত নির্মাণ কর এবং অতঃপর তাকে আগুনের স্তুপে নিক্ষেপ কর।"},
"37:98" : {s: 37, a: 98, e: "They wanted to harm him, but We humiliated them.", b: "তারপর তারা তার বিরুদ্ধে মহা ষড়যন্ত্র আঁটতে চাইল, কিন্তু আমি তাদেরকেই পরাভূত করে দিলাম।"},
"37:99" : {s: 37, a: 99, e: "He said, &lsquo;I will go to my Lord: He is sure to guide me.", b: "সে বললঃ আমি আমার পালনকর্তার দিকে চললাম, তিনি আমাকে পথপ্রদর্শন করবেন।"},
"37:100" : {s: 37, a: 100, e: "Lord, grant me a righteous son,&rsquo;", b: "হে আমার পরওয়ারদেগার! আমাকে এক সৎপুত্র দান কর।"},
"37:101" : {s: 37, a: 101, e: "so We gave him the good news that he would have a patient son.", b: "সুতরাং আমি তাকে এক সহনশীল পুত্রের সুসংবাদ দান করলাম।"},
"37:102" : {s: 37, a: 102, e: "When the boy was old enough to work with his father, Abraham said, &lsquo;My son, I have seen myself sacrificing you in a dream. What do you think?&rsquo; He said, &lsquo;Father, do as you are commanded and, God willing, you will find me steadfast.&rsquo;", b: "অতঃপর সে যখন পিতার সাথে চলাফেরা করার বয়সে উপনীত হল, তখন ইব্রাহীম তাকে বললঃ বৎস! আমি স্বপ্নে দেখিযে, তোমাকে যবেহ করছি; এখন তোমার অভিমত কি দেখ। সে বললঃ পিতাঃ! আপনাকে যা আদেশ করা হয়েছে, তাই করুন। আল্লাহ চাহে তো আপনি আমাকে সবরকারী পাবেন।"},
});
