"39:31:1" : {s: 39, a: 31, w: 1, r: "", t: "ثُمَّ", l: "ثُمّ", lc: 338, tl: "Thumma", lb: "তারপর",e: "Then", b: "এরপর বের করেছেন", i: "kemudian", f: 337, rm: ""},
