window.translation = $.extend(window.translation || {}, {
"44:19" : {s: 44, a: 19, e: "Do not consider yourselves to be above God! I come to you with clear authority.", b: "আর তোমরা আল্লাহর বিরুদ্ধে ঔদ্ধত্য প্রকাশ করো না। আমি তোমাদের কাছে প্রকাশ্য প্রমাণ উপস্থিত করছি।"},
"44:20" : {s: 44, a: 20, e: "I seek refuge in my Lord and yours against your insults!", b: "তোমরা যাতে আমাকে প্রস্তরবর্ষণে হত্যা না কর, তজ্জন্যে আমি আমার পালনকর্তা ও তোমাদের পালনকর্তার শরনাপন্ন হয়েছি।"},
"44:21" : {s: 44, a: 21, e: "If you do not believe me, just let me be.&rsquo;", b: "তোমরা যদি আমার প্রতি বিশ্বাস স্থাপন না কর, তবে আমার কাছ থেকে দূরে থাক।"},
"44:22" : {s: 44, a: 22, e: "[Moses] cried to his Lord, &lsquo;These people are evildoers!&rsquo;", b: "অতঃপর সে তার পালনকর্তার কাছে দোয়া করল যে, এরা অপরাধী সম্প্রদায়।"},
"44:23" : {s: 44, a: 23, e: "[God replied], &lsquo;Escape in the night with My servants, for you are sure to be pursued.", b: "তাহলে তুমি আমার বান্দাদেরকে নিয়ে রাত্রিবেলায় বের হয়ে পড়। নিশ্চয় তোমাদের পশ্চাদ্ধবন করা হবে।"},
"44:24" : {s: 44, a: 24, e: "Leave the sea behind you parted and their army will be drowned.&rsquo;", b: "এবং সমুদ্রকে অচল থাকতে দাও। নিশ্চয় ওরা নিমজ্জত বাহিনী।"},
"44:25" : {s: 44, a: 25, e: "Many a garden and spring they left behind,", b: "তারা ছেড়ে গিয়েছিল কত উদ্যান ও প্রস্রবন,"},
"44:26" : {s: 44, a: 26, e: "many a cornfield and noble building,", b: "কত শস্যক্ষেত্র ও সূরম্য স্থান।"},
"44:27" : {s: 44, a: 27, e: "many a thing in which they had delighted:", b: "কত সুখের উপকরণ, যাতে তারা খোশগল্প করত।"},
"44:28" : {s: 44, a: 28, e: "We gave these to another people to inherit.", b: "এমনিই হয়েছিল এবং আমি ওগুলোর মালিক করেছিলাম ভিন্ন সম্প্রদায়কে।"},
"44:29" : {s: 44, a: 29, e: "Neither heavens nor earth shed a tear for them, nor were they given any time.", b: "তাদের জন্যে ক্রন্দন করেনি আকাশ ও পৃথিবী এবং তারা অবকাশও পায়নি।"},
"44:30" : {s: 44, a: 30, e: "We saved the Children of Israel from their degrading suffering", b: "আমি বনী-ইসরাঈলকে অপমানজনক শাস্তি থেকে উদ্ধার করছি।"},
"44:31" : {s: 44, a: 31, e: "at the hands of Pharaoh: he was a tyrant who exceeded all bounds.", b: "ফেরাউন সে ছিল সীমালংঘনকারীদের মধ্যে শীর্ষস্থানীয়।"},
"44:32" : {s: 44, a: 32, e: "We chose them knowingly above others:", b: "আমি জেনেশুনে তাদেরকে বিশ্ববাসীদের উপর শ্রেষ্ঠত্ব দিয়েছিলাম।"},
"44:33" : {s: 44, a: 33, e: "We gave them revelations in which there was a clear test.", b: "এবং আমি তাদেরকে এমন নিদর্শনাবলী দিয়েছিলাম যাতে ছিল স্পষ্ট সাহায্য।"},
"44:34" : {s: 44, a: 34, e: "These people here assert,", b: "কাফেররা বলেই থাকে,"},
"44:35" : {s: 44, a: 35, e: "&lsquo;There is nothing beyond our one death: we will not be resurrected.", b: "প্রথম মৃত্যুর মাধ্যমেই আমাদের সবকিছুর অবসান হবে এবং আমরা পুনরুত্থিত হব না।"},
"44:36" : {s: 44, a: 36, e: "Bring back our forefathers, if what you say is true.&rsquo;", b: "তোমরা যদি সত্যবাদী হও, তবে আমাদের পূর্বপুরুষদেরকে নিয়ে এস।"},
"44:37" : {s: 44, a: 37, e: "Are they better than the people of Tubbaand those who flourished before them? We destroyed them all- they were guilty.", b: "ওরা শ্রেষ্ঠ, না তুব্বার সম্প্রদায় ও তাদের পূর্ববর্তীরা? আমি ওদেরকে ধ্বংস করে দিয়েছি। ওরা ছিল অপরাধী।"},
"44:38" : {s: 44, a: 38, e: "We were not playing a pointless game when We created the heavens and earth and everything in between;", b: "আমি নভোমন্ডল, ভূমন্ডল ও এতদুভয়ের মধ্যবর্তী সবকিছু ক্রীড়াচ্ছলে সৃষ্টি করিনি।"},
"44:39" : {s: 44, a: 39, e: "We created them for a true purpose, but most people do not comprehend.", b: "আমি এগুলো যথাযথ উদ্দেশ্যে সৃষ্টি করেছি; কিন্তু তাদের অধিকাংশই বোঝে না।"},
});
