window.translation = $.extend(window.translation || {}, {
"56:51" : {s: 56, a: 51, e: "and you who have gone astray and denied the truth", b: "অতঃপর হে পথভ্রষ্ট, মিথ্যারোপকারীগণ।"},
"56:52" : {s: 56, a: 52, e: "will eat from the bitter tree of Zaqqum,", b: "তোমরা অবশ্যই ভক্ষণ করবে যাক্কুম বৃক্ষ থেকে,"},
"56:53" : {s: 56, a: 53, e: "filling your bellies with it,", b: "অতঃপর তা দ্বারা উদর পূর্ণ করবে,"},
"56:54" : {s: 56, a: 54, e: "and drink scalding water,", b: "অতঃপর তার উপর পান করবে উত্তপ্ত পানি।"},
"56:55" : {s: 56, a: 55, e: "lapping it like thirsty camels.&rsquo;", b: "পান করবে পিপাসিত উটের ন্যায়।"},
"56:56" : {s: 56, a: 56, e: "This will be their welcome on the Day of Judgement.", b: "কেয়ামতের দিন এটাই হবে তাদের আপ্যায়ন।"},
"56:57" : {s: 56, a: 57, e: "It was We who created you: will you not believe?", b: "আমি সৃষ্টি করেছি তোমাদেরকে। অতঃপর কেন তোমরা তা সত্য বলে বিশ্বাস কর না।"},
"56:58" : {s: 56, a: 58, e: "Consider [the semen] you eject-", b: "তোমরা কি ভেবে দেখেছ, তোমাদের বীর্যপাত সম্পর্কে।"},
"56:59" : {s: 56, a: 59, e: "do you create it yourselves or are We the Creator?", b: "তোমরা তাকে সৃষ্টি কর, না আমি সৃষ্টি করি?"},
"56:60" : {s: 56, a: 60, e: "We ordained death to be among you. Nothing could stop Us", b: "আমি তোমাদের মৃত্যুকাল নির্ধারিত করেছি এবং আমি অক্ষম নই।"},
"56:61" : {s: 56, a: 61, e: "if We intended to change you and recreate you in a way unknown to you.", b: "এ ব্যাপারে যে, তোমাদের পরিবর্তে তোমাদের মত লোককে নিয়ে আসি এবং তোমাদেরকে এমন করে দেই, যা তোমরা জান না।"},
"56:62" : {s: 56, a: 62, e: "You have learned how you were first created: will you not reflect?", b: "তোমরা অবগত হয়েছ প্রথম সৃষ্টি সম্পর্কে, তবে তোমরা অনুধাবন কর না কেন?"},
"56:63" : {s: 56, a: 63, e: "Consider the seeds you sow in the ground-", b: "তোমরা যে বীজ বপন কর, সে সম্পর্কে ভেবে দেখেছ কি?"},
"56:64" : {s: 56, a: 64, e: "is it you who make them grow or We?", b: "তোমরা তাকে উৎপন্ন কর, না আমি উৎপন্নকারী ?"},
"56:65" : {s: 56, a: 65, e: "If We wished, We could turn your harvest into chaff and leave you to wail,", b: "আমি ইচ্ছা করলে তাকে খড়কুটা করে দিতে পারি, অতঃপর হয়ে যাবে তোমরা বিস্ময়াবিষ্ট।"},
"56:66" : {s: 56, a: 66, e: "&lsquo;We are burdened with debt;", b: "বলবেঃ আমরা তো &#2443ণের চাপে পড়ে গেলাম;"},
"56:67" : {s: 56, a: 67, e: "we are bereft.&rsquo;", b: "বরং আমরা হূত সর্বস্ব হয়ে পড়লাম।"},
"56:68" : {s: 56, a: 68, e: "Consider the water you drink-", b: "তোমরা যে পানি পান কর, সে সম্পর্কে ভেবে দেখেছ কি?"},
"56:69" : {s: 56, a: 69, e: "was it you who brought it down from the rain-cloud or We?", b: "তোমরা তা মেঘ থেকে নামিয়ে আন, না আমি বর্ষন করি?"},
"56:70" : {s: 56, a: 70, e: "If We wanted, We could make it bitter: will you not be thankful?", b: "আমি ইচ্ছা করলে তাকে লোনা করে দিতে পারি, অতঃপর তোমরা কেন কৃতজ্ঞতা প্রকাশ কর না?"},
"56:71" : {s: 56, a: 71, e: "Consider the fire you kindle-", b: "তোমরা যে অগ্নি প্রজ্জ্বলিত কর, সে সম্পর্কে ভেবে দেখেছ কি?"},
"56:72" : {s: 56, a: 72, e: "is it you who make the wood for it grow or We?", b: "তোমরা কি এর বৃক্ষ সৃষ্টি করেছ, না আমি সৃষ্টি করেছি ?"},
"56:73" : {s: 56, a: 73, e: "We made it a reminder, and useful to those who kindle it,", b: "আমি সেই বৃক্ষকে করেছি স্মরণিকা এবং মরুবাসীদের জন্য সামগ্রী।"},
"56:74" : {s: 56, a: 74, e: "so [Prophet] glorify the name of your Lord, the Supreme.", b: "অতএব, আপনি আপনার মহান পালনকর্তার নামে পবিত্রতা ঘোষণা করুন।"},
"56:75" : {s: 56, a: 75, e: "I swear by the positions of the stars-", b: "অতএব, আমি তারকারাজির অস্তাচলের শপথ করছি,"},
"56:76" : {s: 56, a: 76, e: "a mighty oath, if you only knew-", b: "নিশ্চয় এটা এক মহা শপথ-যদি তোমরা জানতে।"},
});
