window.translation = $.extend(window.translation || {}, {
"69:9" : {s: 69, a: 9, e: "Pharaoh, too, and those before him, and the ruined cities: these people committed grave sins and disobeyed the messenger of their Lord, so He seized them with an ever-tightening grip.", b: "ফেরাউন, তাঁর পূর্ববর্তীরা এবং উল্টে যাওয়া বস্তিবাসীরা গুরুতর পাপ করেছিল।"},
"69:10" : {s: 69, a: 10, e: "But when the Flood rose high,", b: "তারা তাদের পালনকর্তার রসূলকে অমান্য করেছিল। ফলে তিনি তাদেরকে কঠোরহস্তে পাকড়াও করলেন।"},
"69:11" : {s: 69, a: 11, e: "We saved you in the floating ship,", b: "যখন জলোচ্ছ্বাস হয়েছিল, তখন আমি তোমাদেরকে চলন্ত নৌযানে আরোহণ করিয়েছিলাম।"},
"69:12" : {s: 69, a: 12, e: "making that event a reminder for you: attentive ears may take heed.", b: "যাতে এ ঘটনা তোমাদের জন্যে স্মৃতির বিষয় এবং কান এটাকে উপদেশ গ্রহণের উপযোগী রূপে গ্রহণ করে।"},
"69:13" : {s: 69, a: 13, e: "When the Trumpet is sounded a single time,", b: "যখন শিংগায় ফুৎকার দেয়া হবে-একটি মাত্র ফুৎকার"},
"69:14" : {s: 69, a: 14, e: "when the earth and its mountains are raised high and then crushed with a single blow,", b: "এবং পৃথিবী ও পর্বতমালা উত্তোলিত হবে ও চুর্ণ-বিচুর্ণ করে দেয়া হবে,"},
"69:15" : {s: 69, a: 15, e: "on that Day the Great Event will come to pass.", b: "সেদিন কেয়ামত সংঘটিত হবে।"},
"69:16" : {s: 69, a: 16, e: "The sky will be torn apart on that Day, it will be so frail.", b: "সেদিন আকাশ বিদীর্ণ হবে ও বিক্ষিপ্ত হবে।"},
"69:17" : {s: 69, a: 17, e: "The angels will be on all sides of it and, on that Day, eight of them will bear the throne of your Lord above them.", b: "এবং ফেরেশতাগণ আকাশের প্রান্তদেশে থাকবে ও আট জন ফেরেশতা আপনার পালনকর্তার আরশকে তাদের উর্ধ্বে বহন করবে।"},
"69:18" : {s: 69, a: 18, e: "On that Day you will be brought to judgement and none of your secrets will remain hidden.", b: "সেদিন তোমাদেরকে উপস্থিত করা হবে। তোমাদের কোন কিছু গোপন থাকবে না।"},
"69:19" : {s: 69, a: 19, e: "Anyone who is given his Record in his right hand will say, &lsquo;Here is my Record, read it.", b: "অতঃপর যার আমলনামা ডান হাতে দেয়া হবে, সে বলবেঃ নাও, তোমরাও আমলনামা পড়ে দেখ।"},
"69:20" : {s: 69, a: 20, e: "I knew I would meet my Reckoning,&rsquo;", b: "আমি জানতাম যে, আমাকে হিসাবের সম্মুখীন হতে হবে।"},
"69:21" : {s: 69, a: 21, e: "and so he will have a pleasant life", b: "অতঃপর সে সুখী জীবন-যাপন করবে,"},
"69:22" : {s: 69, a: 22, e: "in a lofty Garden,", b: "সুউচ্চ জান্নাতে।"},
"69:23" : {s: 69, a: 23, e: "with clustered fruit within his reach.", b: "তার ফলসমূহ অবনমিত থাকবে।"},
"69:24" : {s: 69, a: 24, e: "It will be said, &lsquo;Eat and drink to your heart&rsquo;s content as a reward for what you have done in days gone by.&rsquo;", b: "বিগত দিনে তোমরা যা প্রেরণ করেছিলে, তার প্রতিদানে তোমরা খাও এবং পান কর তৃপ্তি সহকারে।"},
"69:25" : {s: 69, a: 25, e: "But anyone who is given his Record in his left hand will say, &lsquo;If only I had never been given any Record", b: "যার আমলনামা তার বাম হাতে দেয়া হবে, সে বলবেঃ হায় আমায় যদি আমার আমল নামা না দেয়া হতো।"},
"69:26" : {s: 69, a: 26, e: "and knew nothing of my Reckoning.", b: "আমি যদি না জানতাম আমার হিসাব!"},
"69:27" : {s: 69, a: 27, e: "How I wish death had been the end of me.", b: "হায়, আমার মৃত্যুই যদি শেষ হত।"},
"69:28" : {s: 69, a: 28, e: "My wealth has been no use to me,", b: "আমার ধন-সম্পদ আমার কোন উপকারে আসল না।"},
"69:29" : {s: 69, a: 29, e: "and my power has vanished.&rsquo;", b: "আমার ক্ষমতাও বরবাদ হয়ে গেল।"},
"69:30" : {s: 69, a: 30, e: "&lsquo;Take him, put a collar on him,", b: "ফেরেশতাদেরকে বলা হবেঃ ধর একে গলায় বেড়ি পড়িয়ে দাও,"},
"69:31" : {s: 69, a: 31, e: "lead him to burn in the blazing Fire,", b: "অতঃপর নিক্ষেপ কর জাহান্নামে।"},
"69:32" : {s: 69, a: 32, e: "and [bind him] in a chain seventy metres long:", b: "অতঃপর তাকে শৃঙ্খলিত কর সত্তর গজ দীর্ঘ এক শিকলে।"},
"69:33" : {s: 69, a: 33, e: "he would not believe in Almighty God,", b: "নিশ্চয় সে মহান আল্লাহতে বিশ্বাসী ছিল না।"},
"69:34" : {s: 69, a: 34, e: "he never encouraged feeding the hungry,", b: "এবং মিসকীনকে আহার্য দিতে উৎসাহিত করত না।"},
});
