window.translation = $.extend(window.translation || {}, {
"69:35" : {s: 69, a: 35, e: "so today he has no real friend here,", b: "অতএব, আজকের দিন এখানে তার কোন সুহূদ নাই।"},
"69:36" : {s: 69, a: 36, e: "and the only food he has is the filth", b: "এবং কোন খাদ্য নাই, ক্ষত-নিঃসৃত পুঁজ ব্যতীত।"},
"69:37" : {s: 69, a: 37, e: "that only sinners eat.&rsquo;", b: "গোনাহগার ব্যতীত কেউ এটা খাবে না।"},
"69:38" : {s: 69, a: 38, e: "So I swear by what you can see", b: "তোমরা যা দেখ, আমি তার শপথ করছি।"},
"69:39" : {s: 69, a: 39, e: "and by what you cannot see:", b: "এবং যা তোমরা দেখ না, তার-"},
"69:40" : {s: 69, a: 40, e: "this [Quran] is the speech of an honoured messenger,", b: "নিশ্চয়ই এই কোরআন একজন সম্মানিত রসূলের আনীত।"},
"69:41" : {s: 69, a: 41, e: "not the words of a poet- how little you believe!-", b: "এবং এটা কোন কবির কালাম নয়; তোমরা কমই বিশ্বাস কর।"},
"69:42" : {s: 69, a: 42, e: "nor the words of a soothsayer- how little you reflect!", b: "এবং এটা কোন অতীন্দ্রিয়বাদীর কথা নয়; তোমরা কমই অনুধাবন কর।"},
"69:43" : {s: 69, a: 43, e: "This [Quran] is a message sent down from the Lord of the Worlds:", b: "এটা বিশ্বপালনকর্তার কাছ থেকে অবতীর্ণ।"},
"69:44" : {s: 69, a: 44, e: "if [the Prophet] had attributed some fabrication to Us,", b: "সে যদি আমার নামে কোন কথা রচনা করত,"},
"69:45" : {s: 69, a: 45, e: "We would certainly have seized his right hand", b: "তবে আমি তার দক্ষিণ হস্ত ধরে ফেলতাম,"},
"69:46" : {s: 69, a: 46, e: "and cut off his lifeblood,", b: "অতঃপর কেটে দিতাম তার গ্রীবা।"},
"69:47" : {s: 69, a: 47, e: "and none of you could have defended him.", b: "তোমাদের কেউ তাকে রক্ষা করতে পারতে না।"},
"69:48" : {s: 69, a: 48, e: "This [Quran] is a reminder for those who are aware of God.", b: "এটা খোদাভীরুদের জন্যে অবশ্যই একটি উপদেশ।"},
"69:49" : {s: 69, a: 49, e: "We know that some of you consider it to be lies-", b: "আমি জানি যে, তোমাদের মধ্যে কেউ কেউ মিথ্যারোপ করবে।"},
"69:50" : {s: 69, a: 50, e: "this will be a source of bitter regret for the disbelievers-", b: "নিশ্চয় এটা কাফেরদের জন্যে অনুতাপের কারণ।"},
"69:51" : {s: 69, a: 51, e: "but it is in fact the certain Truth.", b: "নিশ্চয় এটা নিশ্চিত সত্য।"},
"69:52" : {s: 69, a: 52, e: "So [Prophet] glorify the name of your Lord, the Almighty.", b: "অতএব, আপনি আপনার মহান পালনকর্তার নামের পবিত্রতা বর্ননা করুন।"},
"70:1" : {s: 70, a: 1, e: "A man [mockingly] demanded the punishment.", b: "একব্যক্তি চাইল, সেই আযাব সংঘটিত হোক যা অবধারিত-"},
"70:2" : {s: 70, a: 2, e: "It will fall on the disbelievers&ndash;&ndash;none can deflect it&ndash;&ndash;", b: "কাফেরদের জন্যে, যার প্রতিরোধকারী কেউ নেই।"},
"70:3" : {s: 70, a: 3, e: "from God, the Lord of the Ways of Ascent,", b: "তা আসবে আল্লাহ তাâ€™আলার পক্ষ থেকে, যিনি সমুন্নত মর্তবার অধিকারী।"},
"70:4" : {s: 70, a: 4, e: "by which the angels and the Spirit ascend to Him, on a Day whose length is fifty thousand years.", b: "ফেরেশতাগণ এবং রূহ আল্লাহ তাâ€™আলার দিকে উর্ধ্বগামী হয় এমন একদিনে, যার পরিমাণ পঞ্চাশ হাজার বছর।"},
"70:5" : {s: 70, a: 5, e: "So be patient, [Prophet], as befits you.", b: "অতএব, আপনি উত্তম সবর করুন।"},
"70:6" : {s: 70, a: 6, e: "The disbelievers think it is distant,", b: "তারা এই আযাবকে সুদূরপরাহত মনে করে,"},
"70:7" : {s: 70, a: 7, e: "but We know it to be close.", b: "আর আমি একে আসন্ন দেখছি।"},
"70:8" : {s: 70, a: 8, e: "On a Day when the heavens will be like molten brass", b: "সেদিন আকাশ হবে গলিত তামার মত।"},
"70:9" : {s: 70, a: 9, e: "and the mountains like tufts of wool,", b: "এবং পর্বতসমূহ হবে রঙ্গীন পশমের মত,"},
"70:10" : {s: 70, a: 10, e: "when no friend will ask about his friend,", b: "বন্ধু বন্ধুর খবর নিবে না।"},
});
