window.translation = $.extend(window.translation || {}, {
"74:48" : {s: 74, a: 48, e: "No intercessor&rsquo;s plea will benefit them now.", b: "অতএব, সুপারিশকারীদের সুপারিশ তাদের কোন উপকারে আসবে না।"},
"74:49" : {s: 74, a: 49, e: "What is the matter with them? Why do they turn away from the warning,", b: "তাদের কি হল যে, তারা উপদেশ থেকে মুখ ফিরিয়ে নেয়?"},
"74:50" : {s: 74, a: 50, e: "like frightened asses", b: "যেন তারা ইতস্ততঃ বিক্ষিপ্ত গর্দভ।"},
"74:51" : {s: 74, a: 51, e: "fleeing from a lion?", b: "হট্টগোলের কারণে পলায়নপর।"},
"74:52" : {s: 74, a: 52, e: "Each one of them demands that a scripture be sent down to him and unrolled before his very eyes-", b: "বরং তাদের প্রত্যেকেই চায় তাদের প্রত্যেককে একটি উম্মুক্ত গ্রন্থ দেয়া হোক।"},
"74:53" : {s: 74, a: 53, e: "No! Truly they have no fear of the life to come-", b: "কখনও না, বরং তারা পরকালকে ভয় করে না।"},
"74:54" : {s: 74, a: 54, e: "but truly this is a reminder.", b: "কখনও না, এটা তো উপদেশ মাত্র।"},
"74:55" : {s: 74, a: 55, e: "Let whoever wishes to take heed do so:", b: "অতএব, যার ইচ্ছা, সে একে স্মরণ করুক।"},
"74:56" : {s: 74, a: 56, e: "they will only take heed if God so wishes. He is the Lord who should be heeded, the Lord of forgiveness.", b: "তারা স্মরণ করবে না, কিন্তু যদি আল্লাহ চান। তিনিই ভয়ের যোগ্য এবং ক্ষমার অধিকারী।"},
"75:1" : {s: 75, a: 1, e: "By the Day of Resurrection", b: "আমি শপথ করি কেয়ামত দিবসের,"},
"75:2" : {s: 75, a: 2, e: "and by the self-reproaching soul!", b: "আরও শপথ করি সেই মনের, যে নিজেকে ধিক্কার দেয়-"},
"75:3" : {s: 75, a: 3, e: "Does man think We shall not put his bones back together?", b: "মানুষ কি মনে করে যে আমি তার অস্থিসমূহ একত্রিত করব না?"},
"75:4" : {s: 75, a: 4, e: "In fact, We can reshape his very fingertips.", b: "পরন্ত আমি তার অংগুলিগুলো পর্যন্ত সঠিকভাবে সন্নিবেশিত করতে সক্ষম।"},
"75:5" : {s: 75, a: 5, e: "Yet man wants to deny what is ahead of him:", b: "বরং মানুষ তার ভবিষ্যত জীবনেও ধৃষ্টতা করতে চায়"},
"75:6" : {s: 75, a: 6, e: "he says, &lsquo;So, when will this Day of Resurrection be?&rsquo;", b: "সে প্রশ্ন করে-কেয়ামত দিবস কবে?"},
"75:7" : {s: 75, a: 7, e: "When eyes are dazzled", b: "যখন দৃষ্টি চমকে যাবে,"},
"75:8" : {s: 75, a: 8, e: "and the moon eclipsed,", b: "চন্দ্র জ্যোতিহীন হয়ে যাবে।"},
"75:9" : {s: 75, a: 9, e: "when the sun and the moon are brought together,", b: "এবং সূর্য ও চন্দ্রকে একত্রিত করা হবে-"},
"75:10" : {s: 75, a: 10, e: "on that Day man will say, &lsquo;Where can I escape?&rsquo;", b: "সে দিন মানুষ বলবেঃ পলায়নের জায়গা কোথায় ?"},
"75:11" : {s: 75, a: 11, e: "Truly, there is no refuge:", b: "না কোথাও আশ্রয়স্থল নেই।"},
"75:12" : {s: 75, a: 12, e: "they will all return to your Lord on that Day.", b: "আপনার পালনকর্তার কাছেই সেদিন ঠাঁই হবে।"},
"75:13" : {s: 75, a: 13, e: "On that Day, man will be told what he put first and what he put last.", b: "সেদিন মানুষকে অবহিত করা হবে সে যা সামনে প্রেরণ করেছে ও পশ্চাতে ছেড়ে দিয়েছে।"},
"75:14" : {s: 75, a: 14, e: "Truly, man is a clear witness against himself,", b: "বরং মানুষ নিজেই তার নিজের সম্পর্কে চক্ষুমান।"},
"75:15" : {s: 75, a: 15, e: "despite all the excuses he may put forward.", b: "যদিও সে তার অজুহাত পেশ করতে চাইবে।"},
"75:16" : {s: 75, a: 16, e: "[Prophet], do not rush your tongue in an attempt to hasten [your memorization of] the Revelation:", b: "তাড়াতাড়ি শিখে নেয়ার জন্যে আপনি দ্রুত ওহী আবৃত্তি করবেন না।"},
"75:17" : {s: 75, a: 17, e: "We shall make sure of its safe collection and recitation.", b: "এর সংরক্ষণ ও পাঠ আমারই দায়িত্ব।"},
"75:18" : {s: 75, a: 18, e: "When We have recited it, repeat the recitation", b: "অতঃপর আমি যখন তা পাঠ করি, তখন আপনি সেই পাঠের অনুসরণ করুন।"},
"75:19" : {s: 75, a: 19, e: "and We shall make it clear.", b: "এরপর বিশদ বর্ণনা আমারই দায়িত্ব।"},
});
