window.translation = $.extend(window.translation || {}, {
"78:1" : {s: 78, a: 1, e: "What are they asking about?", b: "তারা পরস্পরে কি বিষয়ে জিজ্ঞাসাবাদ করছে?"},
"78:2" : {s: 78, a: 2, e: "The momentous announcement", b: "মহা সংবাদ সম্পর্কে,"},
"78:3" : {s: 78, a: 3, e: "about which they differ.", b: "যে সম্পর্কে তারা মতানৈক্য করে।"},
"78:4" : {s: 78, a: 4, e: "They will find out.", b: "না, সত্ত্বরই তারা জানতে পারবে,"},
"78:5" : {s: 78, a: 5, e: "In the end they will find out.", b: "অতঃপর না, সত্বর তারা জানতে পারবে।"},
"78:6" : {s: 78, a: 6, e: "Did We not make the earth smooth,", b: "আমি কি করিনি ভূমিকে বিছানা"},
"78:7" : {s: 78, a: 7, e: "and make the mountains to keep it stable?", b: "এবং পর্বতমালাকে পেরেক?"},
"78:8" : {s: 78, a: 8, e: "Did We not create you in pairs,", b: "আমি তোমাদেরকে জোড়া জোড়া সৃষ্টি করেছি,"},
"78:9" : {s: 78, a: 9, e: "give you sleep for rest,", b: "তোমাদের নিদ্রাকে করেছি ক্লান্তি দূরকারী,"},
"78:10" : {s: 78, a: 10, e: "the night as a cover,", b: "রাত্রিকে করেছি আবরণ।"},
"78:11" : {s: 78, a: 11, e: "and the day for your livelihood?", b: "দিনকে করেছি জীবিকা অর্জনের সময়,"},
"78:12" : {s: 78, a: 12, e: "Did We not build seven strong [heavens] above you,", b: "নির্মান করেছি তোমাদের মাথার উপর মজবুত সপ্ত-আকাশ।"},
"78:13" : {s: 78, a: 13, e: "and make a blazing lamp?", b: "এবং একটি উজ্জ্বল প্রদীপ সৃষ্টি করেছি।"},
"78:14" : {s: 78, a: 14, e: "Did We not send water pouring down from the clouds", b: "আমি জলধর মেঘমালা থেকে প্রচুর বৃষ্টিপাত করি,"},
"78:15" : {s: 78, a: 15, e: "to bring forth with it grain, plants,", b: "যাতে তদ্দ্বারা উৎপন্ন করি শস্য, উদ্ভিদ।"},
"78:16" : {s: 78, a: 16, e: "and luxuriant gardens?", b: "ও পাতাঘন উদ্যান।"},
"78:17" : {s: 78, a: 17, e: "A time has been appointed for the Day of Decision:", b: "নিশ্চয় বিচার দিবস নির্ধারিত রয়েছে।"},
"78:18" : {s: 78, a: 18, e: "a Day when the Trumpet will sound and you will come forward in crowds,", b: "যেদিন শিংগায় ফুঁক দেয়া হবে, তখন তোমরা দলে দলে সমাগত হবে।"},
"78:19" : {s: 78, a: 19, e: "when the sky will open up like wide portals,", b: "আকাশ বিদীর্ণ হয়ে; তাতে বহু দরজা সৃষ্টি হবে।"},
"78:20" : {s: 78, a: 20, e: "when the mountains will vanish like a mirage.", b: "এবং পর্বতমালা চালিত হয়ে মরীচিকা হয়ে যাবে।"},
"78:21" : {s: 78, a: 21, e: "Hell lies in wait,", b: "নিশ্চয় জাহান্নাম প্রতীক্ষায় থাকবে,"},
"78:22" : {s: 78, a: 22, e: "a home for oppressors", b: "সীমালংঘনকারীদের আশ্রয়স্থলরূপে।"},
"78:23" : {s: 78, a: 23, e: "to stay in for a long, long time,", b: "তারা তথায় শতাব্দীর পর শতাব্দী অবস্থান করবে।"},
"78:24" : {s: 78, a: 24, e: "where they will taste no coolness nor drink", b: "তথায় তারা কোন শীতল এবং পানীয় আস্বাদন করবে না;"},
"78:25" : {s: 78, a: 25, e: "except one that is scalding and dark&ndash;&ndash;", b: "কিন্তু ফুটন্ত পানি ও পূঁজ পাবে।"},
"78:26" : {s: 78, a: 26, e: "a fitting requital,", b: "পরিপূর্ণ প্রতিফল হিসেবে।"},
"78:27" : {s: 78, a: 27, e: "for they did not fear a reckoning,", b: "নিশ্চয় তারা হিসাব-নিকাশ আশা করত না।"},
"78:28" : {s: 78, a: 28, e: "and they rejected Our messages as lies.", b: "এবং আমার আয়াতসমূহে পুরোপুরি মিথ্যারোপ করত।"},
"78:29" : {s: 78, a: 29, e: "We have recorded everything in a Record.", b: "আমি সবকিছুই লিপিবদ্ধ করে সংরক্ষিত করেছি।"},
"78:30" : {s: 78, a: 30, e: "&lsquo;Taste this: all you will get from Us is more torment.&rsquo;", b: "অতএব, তোমরা আস্বাদন কর, আমি কেবল তোমাদের শাস্তিই বৃদ্ধি করব।"},
});
