window.translation = $.extend(window.translation || {}, {
"78:1" : {s: 78, a: 1, e: "What are they asking about?", b: "তারা পরস্পরে কি বিষয়ে জিজ্ঞাসাবাদ করছে?", t: "بِسْمِ ٱللَّهِ ٱلرَّحْمَٰنِ ٱلرَّحِيمِ عَمَّ يَتَسَآءَلُونَ"},
"78:2" : {s: 78, a: 2, e: "The momentous announcement", b: "মহা সংবাদ সম্পর্কে,", t: "عَنِ ٱلنَّبَإِ ٱلْعَظِيمِ"},
"78:3" : {s: 78, a: 3, e: "about which they differ.", b: "যে সম্পর্কে তারা মতানৈক্য করে।", t: "ٱلَّذِى هُمْ فِيهِ مُخْتَلِفُونَ"},
"78:4" : {s: 78, a: 4, e: "They will find out.", b: "না, সত্ত্বরই তারা জানতে পারবে,", t: "كَلَّا سَيَعْلَمُونَ"},
"78:5" : {s: 78, a: 5, e: "In the end they will find out.", b: "অতঃপর না, সত্বর তারা জানতে পারবে।", t: "ثُمَّ كَلَّا سَيَعْلَمُونَ"},
"78:6" : {s: 78, a: 6, e: "Did We not make the earth smooth,", b: "আমি কি করিনি ভূমিকে বিছানা", t: "أَلَمْ نَجْعَلِ ٱلْأَرْضَ مِهَٰدًا"},
"78:7" : {s: 78, a: 7, e: "and make the mountains to keep it stable?", b: "এবং পর্বতমালাকে পেরেক?", t: "وَٱلْجِبَالَ أَوْتَادًا"},
"78:8" : {s: 78, a: 8, e: "Did We not create you in pairs,", b: "আমি তোমাদেরকে জোড়া জোড়া সৃষ্টি করেছি,", t: "وَخَلَقْنَٰكُمْ أَزْوَٰجًا"},
"78:9" : {s: 78, a: 9, e: "give you sleep for rest,", b: "তোমাদের নিদ্রাকে করেছি ক্লান্তি দূরকারী,", t: "وَجَعَلْنَا نَوْمَكُمْ سُبَاتًا"},
"78:10" : {s: 78, a: 10, e: "the night as a cover,", b: "রাত্রিকে করেছি আবরণ।", t: "وَجَعَلْنَا ٱلَّيْلَ لِبَاسًا"},
"78:11" : {s: 78, a: 11, e: "and the day for your livelihood?", b: "দিনকে করেছি জীবিকা অর্জনের সময়,", t: "وَجَعَلْنَا ٱلنَّهَارَ مَعَاشًا"},
"78:12" : {s: 78, a: 12, e: "Did We not build seven strong [heavens] above you,", b: "নির্মান করেছি তোমাদের মাথার উপর মজবুত সপ্ত-আকাশ।", t: "وَبَنَيْنَا فَوْقَكُمْ سَبْعًا شِدَادًا"},
"78:13" : {s: 78, a: 13, e: "and make a blazing lamp?", b: "এবং একটি উজ্জ্বল প্রদীপ সৃষ্টি করেছি।", t: "وَجَعَلْنَا سِرَاجًا وَهَّاجًا"},
"78:14" : {s: 78, a: 14, e: "Did We not send water pouring down from the clouds", b: "আমি জলধর মেঘমালা থেকে প্রচুর বৃষ্টিপাত করি,", t: "وَأَنزَلْنَا مِنَ ٱلْمُعْصِرَٰتِ مَآءً ثَجَّاجًا"},
"78:15" : {s: 78, a: 15, e: "to bring forth with it grain, plants,", b: "যাতে তদ্দ্বারা উৎপন্ন করি শস্য, উদ্ভিদ।", t: "لِّنُخْرِجَ بِهِۦ حَبًّا وَنَبَاتًا"},
"78:16" : {s: 78, a: 16, e: "and luxuriant gardens?", b: "ও পাতাঘন উদ্যান।", t: "وَجَنَّٰتٍ أَلْفَافًا"},
"78:17" : {s: 78, a: 17, e: "A time has been appointed for the Day of Decision:", b: "নিশ্চয় বিচার দিবস নির্ধারিত রয়েছে।", t: "إِنَّ يَوْمَ ٱلْفَصْلِ كَانَ مِيقَٰتًا"},
"78:18" : {s: 78, a: 18, e: "a Day when the Trumpet will sound and you will come forward in crowds,", b: "যেদিন শিংগায় ফুঁক দেয়া হবে, তখন তোমরা দলে দলে সমাগত হবে।", t: "يَوْمَ يُنفَخُ فِى ٱلصُّورِ فَتَأْتُونَ أَفْوَاجًا"},
"78:19" : {s: 78, a: 19, e: "when the sky will open up like wide portals,", b: "আকাশ বিদীর্ণ হয়ে; তাতে বহু দরজা সৃষ্টি হবে।", t: "وَفُتِحَتِ ٱلسَّمَآءُ فَكَانَتْ أَبْوَٰبًا"},
"78:20" : {s: 78, a: 20, e: "when the mountains will vanish like a mirage.", b: "এবং পর্বতমালা চালিত হয়ে মরীচিকা হয়ে যাবে।", t: "وَسُيِّرَتِ ٱلْجِبَالُ فَكَانَتْ سَرَابًا"},
"78:21" : {s: 78, a: 21, e: "Hell lies in wait,", b: "নিশ্চয় জাহান্নাম প্রতীক্ষায় থাকবে,", t: "إِنَّ جَهَنَّمَ كَانَتْ مِرْصَادًا"},
"78:22" : {s: 78, a: 22, e: "a home for oppressors", b: "সীমালংঘনকারীদের আশ্রয়স্থলরূপে।", t: "لِّلطَّٰغِينَ مَـَٔابًا"},
"78:23" : {s: 78, a: 23, e: "to stay in for a long, long time,", b: "তারা তথায় শতাব্দীর পর শতাব্দী অবস্থান করবে।", t: "لَّٰبِثِينَ فِيهَآ أَحْقَابًا"},
"78:24" : {s: 78, a: 24, e: "where they will taste no coolness nor drink", b: "তথায় তারা কোন শীতল এবং পানীয় আস্বাদন করবে না;", t: "لَّا يَذُوقُونَ فِيهَا بَرْدًا وَلَا شَرَابًا"},
"78:25" : {s: 78, a: 25, e: "except one that is scalding and dark&ndash;&ndash;", b: "কিন্তু ফুটন্ত পানি ও পূঁজ পাবে।", t: "إِلَّا حَمِيمًا وَغَسَّاقًا"},
"78:26" : {s: 78, a: 26, e: "a fitting requital,", b: "পরিপূর্ণ প্রতিফল হিসেবে।", t: "جَزَآءً وِفَاقًا"},
"78:27" : {s: 78, a: 27, e: "for they did not fear a reckoning,", b: "নিশ্চয় তারা হিসাব-নিকাশ আশা করত না।", t: "إِنَّهُمْ كَانُوا۟ لَا يَرْجُونَ حِسَابًا"},
"78:28" : {s: 78, a: 28, e: "and they rejected Our messages as lies.", b: "এবং আমার আয়াতসমূহে পুরোপুরি মিথ্যারোপ করত।", t: "وَكَذَّبُوا۟ بِـَٔايَٰتِنَا كِذَّابًا"},
"78:29" : {s: 78, a: 29, e: "We have recorded everything in a Record.", b: "আমি সবকিছুই লিপিবদ্ধ করে সংরক্ষিত করেছি।", t: "وَكُلَّ شَىْءٍ أَحْصَيْنَٰهُ كِتَٰبًا"},
"78:30" : {s: 78, a: 30, e: "&lsquo;Taste this: all you will get from Us is more torment.&rsquo;", b: "অতএব, তোমরা আস্বাদন কর, আমি কেবল তোমাদের শাস্তিই বৃদ্ধি করব।", t: "فَذُوقُوا۟ فَلَن نَّزِيدَكُمْ إِلَّا عَذَابًا"},
});
