window.translation = $.extend(window.translation || {}, {
"78:31" : {s: 78, a: 31, e: "For those who were aware of God there is supreme fulfilment:", b: "পরহেযগারদের জন্যে রয়েছে সাফল্য।"},
"78:32" : {s: 78, a: 32, e: "private gardens, vineyards,", b: "উদ্যান, আঙ্গুর,"},
"78:33" : {s: 78, a: 33, e: "nubile, well-matched companions,", b: "সমবয়স্কা, পূর্ণযৌবনা তরুণী।"},
"78:34" : {s: 78, a: 34, e: "and an overflowing cup.", b: "এবং পূর্ণ পানপাত্র।"},
"78:35" : {s: 78, a: 35, e: "There they will hear no vain or lying talk:", b: "তারা তথায় অসার ও মিথ্যা বাক্য শুনবে না।"},
"78:36" : {s: 78, a: 36, e: "a reward from your Lord, a fitting gift", b: "এটা আপনার পালনকর্তার তরফ থেকে যথোচিত দান,"},
"78:37" : {s: 78, a: 37, e: "from the Lord of the heavens and earth and everything between, the Lord of Mercy. They will have no authority from Him to speak.", b: "যিনি নভোমন্ডল, ভূমন্ডল ও এতদুভয়ের মধ্যবর্তী সবকিছুর পালনকর্তা, দয়াময়, কেউ তাঁর সাথে কথার অধিকারী হবে না।"},
"78:38" : {s: 78, a: 38, e: "On the Day when the Spirit and the angels stand in rows, they will not speak except for those to whom the Lord of Mercy gives permission, who will say only what is right.", b: "যেদিন রূহ ও ফেরেশতাগণ সারিবদ্ধভাবে দাঁড়াবে। দয়াময় আল্লাহ যাকে অনুমতি দিবেন, সে ব্যতিত কেউ কথা বলতে পারবে না এবং সে সত্যকথা বলবে।"},
"78:39" : {s: 78, a: 39, e: "That is the Day of Truth. So whoever wishes to do so should take the path that leads to his Lord.", b: "এই দিবস সত্য। অতঃপর যার ইচ্ছা, সে তার পালনকর্তার কাছে ঠিকানা তৈরী করুক।"},
"78:40" : {s: 78, a: 40, e: "We have warned you of imminent torment, on the Day when every person will see what their own hands have sent ahead for them, when the disbeliever will say, &lsquo;If only I were dust!&rsquo;", b: "আমি তোমাদেরকে আসন্ন শাস্তি সম্পর্কে সতর্ক করলাম, যেদিন মানুষ প্রত্যেক্ষ করবে যা সে সামনে প্রেরণ করেছে এবং কাফের বলবেঃ হায়, আফসোস-আমি যদি মাটি হয়ে যেতাম।"},
"79:1" : {s: 79, a: 1, e: "By the forceful chargers", b: "শপথ সেই ফেরেশতাগণের, যারা ডুব দিয়ে আত্মা উৎপাটন করে,"},
"79:2" : {s: 79, a: 2, e: "raring to go,", b: "শপথ তাদের, যারা আত্মার বাঁধন খুলে দেয় মৃদুভাবে;"},
"79:3" : {s: 79, a: 3, e: "sweeping ahead at full stretch,", b: "শপথ তাদের, যারা সন্তরণ করে দ্রুতগতিতে,"},
"79:4" : {s: 79, a: 4, e: "overtaking swiftly", b: "শপথ তাদের, যারা দ্রুতগতিতে অগ্রসর হয় এবং"},
"79:5" : {s: 79, a: 5, e: "to bring the matter to an end,", b: "শপথ তাদের, যারা সকল কর্মনির্বাহ করে, কেয়ামত অবশ্যই হবে।"},
"79:6" : {s: 79, a: 6, e: "on the Day when the blast reverberates", b: "যেদিন প্রকম্পিত করবে প্রকম্পিতকারী,"},
"79:7" : {s: 79, a: 7, e: "and the second blast follows,", b: "অতঃপর পশ্চাতে আসবে পশ্চাদগামী;"},
"79:8" : {s: 79, a: 8, e: "hearts will tremble", b: "সেদিন অনেক হৃদয় ভীত-বিহবল হবে।"},
"79:9" : {s: 79, a: 9, e: "and eyes will be downcast.", b: "তাদের দৃষ্টি নত হবে।"},
"79:10" : {s: 79, a: 10, e: "They say, &lsquo;What? shall we be brought back to life,", b: "তারা বলেঃ আমরা কি উলটো পায়ে প্রত্যাবর্তিত হবই-"},
"79:11" : {s: 79, a: 11, e: "after we have turned into decayed bones?&rsquo; and they say,", b: "গলিত অস্থি হয়ে যাওয়ার পরও?"},
"79:12" : {s: 79, a: 12, e: "&lsquo;Such a return is impossible!&rsquo;", b: "তবে তো এ প্রত্যাবর্তন সর্বনাশা হবে!"},
"79:13" : {s: 79, a: 13, e: "But all it will take is a single blast,", b: "অতএব, এটা তো কেবল এক মহা-নাদ,"},
"79:14" : {s: 79, a: 14, e: "and they will be back above ground.", b: "তখনই তারা ময়দানে আবির্ভূত হবে।"},
"79:15" : {s: 79, a: 15, e: "Have you [Prophet] heard the story of Moses?", b: "মূসার বৃত্তান্ত আপনার কাছে পৌছেছে কি?"},
});
