window.translation = $.extend(window.translation || {}, {
"83:35" : {s: 83, a: 35, e: "as they sit on couches, gazing around.", b: "সিংহাসনে বসে, তাদেরকে অবলোকন করছে,"},
"83:36" : {s: 83, a: 36, e: "Have the disbelievers [not] been repaid for their deeds?", b: "কাফেররা যা করত, তার প্রতিফল পেয়েছে তো?"},
"84:1" : {s: 84, a: 1, e: "When the sky is ripped apart,", b: "যখন আকাশ বিদীর্ণ হবে,"},
"84:2" : {s: 84, a: 2, e: "obeying its Lord as it rightly must,", b: "ও তার পালনকর্তার আদেশ পালন করবে এবং আকাশ এরই উপযুক্ত"},
"84:3" : {s: 84, a: 3, e: "when the earth is levelled out,", b: "এবং যখন পৃথিবীকে সম্প্রসারিত করা হবে।"},
"84:4" : {s: 84, a: 4, e: "casts out its contents, and becomes empty,", b: "এবং পৃথিবী তার গর্ভস্থিত সবকিছু বাইরে নিক্ষেপ করবে ও শুন্যগর্ভ হয়ে যাবে।"},
"84:5" : {s: 84, a: 5, e: "obeying its Lord as it rightly must,", b: "এবং তার পালনকর্তার আদেশ পালন করবে এবং পৃথিবী এরই উপযুক্ত।"},
"84:6" : {s: 84, a: 6, e: "you humans, toiling laboriously towards your Lord, will meet Him:", b: "হে মানুষ, তোমাকে তোমরা পালনকর্তা পর্যন্ত পৌছতে কষ্ট স্বীকার করতে হবে, অতঃপর তার সাক্ষাৎ ঘটবে।"},
"84:7" : {s: 84, a: 7, e: "whoever is given his record in his right hand", b: "যাকে তার আমলনামা ডান হাতে দেয়া হবে"},
"84:8" : {s: 84, a: 8, e: "will have an easy reckoning", b: "তার হিসাব-নিকাশ সহজে হয়ে যাবে"},
"84:9" : {s: 84, a: 9, e: "and return to his people well pleased,", b: "এবং সে তার পরিবার-পরিজনের কাছে হৃষ্টচিত্তে ফিরে যাবে"},
"84:10" : {s: 84, a: 10, e: "but whoever is given his record from behind his back", b: "এবং যাকে তার আমলনামা পিঠের পশ্চাদ্দিক থেকে দেয়া, হবে,"},
"84:11" : {s: 84, a: 11, e: "will cry out for destruction&ndash;&ndash;", b: "সে মৃত্যুকে আহবান করবে,"},
"84:12" : {s: 84, a: 12, e: "he will burn in the blazing Fire.", b: "এবং জাহান্নামে প্রবেশ করবে।"},
"84:13" : {s: 84, a: 13, e: "He used to live among his people well pleased.", b: "সে তার পরিবার-পরিজনের মধ্যে আনন্দিত ছিল।"},
"84:14" : {s: 84, a: 14, e: "He thought he would never return [to his Lord]&ndash;&ndash;", b: "সে মনে করত যে, সে কখনও ফিরে যাবে না।"},
"84:15" : {s: 84, a: 15, e: "indeed he will! His Lord was watching him.", b: "কেন যাবে না, তার পালনকর্তা তো তাকে দেখতেন।"},
"84:16" : {s: 84, a: 16, e: "I swear by the glow of sunset,", b: "আমি শপথ করি সন্ধ্যাকালীন লাল আভার"},
"84:17" : {s: 84, a: 17, e: "by the night and what it covers,", b: "এবং রাত্রির, এবং তাতে যার সমাবেশ ঘটে"},
"84:18" : {s: 84, a: 18, e: "by the full moon,", b: "এবং চন্দ্রের, যখন তা পূর্ণরূপ লাভ করে,"},
"84:19" : {s: 84, a: 19, e: "you will progress from stage to stage.", b: "নিশ্চয় তোমরা এক সিঁড়ি থেকে আরেক সিঁড়িতে আরোহণ করবে।"},
"84:20" : {s: 84, a: 20, e: "So why do they not believe?", b: "অতএব, তাদের কি হল যে, তারা ঈমান আনে না?"},
"84:21" : {s: 84, a: 21, e: "Why, when the Quran is read to them, do they not prostrate themselves [to God]?", b: "যখন তাদের কাছে কোরআন পাঠ করা হয়, তখন সেজদা করে না।"},
"84:22" : {s: 84, a: 22, e: "No! The disbelievers reject the Quran&ndash;&ndash;", b: "বরং কাফেররা এর প্রতি মিথ্যারোপ করে।"},
"84:23" : {s: 84, a: 23, e: "God knows best what they keep hidden inside&ndash;&ndash;", b: "তারা যা সংরক্ষণ করে, আল্লাহ তা জানেন।"},
"84:24" : {s: 84, a: 24, e: "so give them news of a painful torment.", b: "অতএব, তাদেরকে যন্ত্রণাদায়ক শাস্তির সুসংবাদ দিন।"},
"84:25" : {s: 84, a: 25, e: "But those who believe and do good deeds will have a never-ending reward.", b: "কিন্তু যারা বিশ্বাস স্থাপন করে ও সৎকর্ম করে, তাদের জন্য রয়েছে অফুরন্ত পুরস্কার।"},
});
