window.translation = $.extend(window.translation || {}, {
"95:1" : {s: 95, a: 1, e: "By the fig, by the olive,", b: "শপথ আঞ্জীর (ডুমুর) ও যয়তুনের,"},
"95:2" : {s: 95, a: 2, e: "by Mount Sinai,", b: "এবং সিনাই প্রান্তরস্থ তূর পর্বতের,"},
"95:3" : {s: 95, a: 3, e: "by this safe town,", b: "এবং এই নিরাপদ নগরীর।"},
"95:4" : {s: 95, a: 4, e: "We create man in the finest state", b: "আমি সৃষ্টি করেছি মানুষকে সুন্দরতর অবয়বে।"},
"95:5" : {s: 95, a: 5, e: "then reduce him to the lowest of the low,", b: "অতঃপর তাকে ফিরিয়ে দিয়েছি নীচ থেকে নীচে।"},
"95:6" : {s: 95, a: 6, e: "except those who believe and do good deeds&ndash;&ndash;", b: "কিন্তু যারা বিশ্বাস স্থাপন করেছে ও সৎকর্ম করেছে, তাদের জন্যে রয়েছে অশেষ পুরস্কার।"},
"95:7" : {s: 95, a: 7, e: "they will have an unfailing reward. After this, what makes you [man] deny the Judgement?", b: "অতঃপর কেন তুমি অবিশ্বাস করছ কেয়ামতকে?"},
"95:8" : {s: 95, a: 8, e: "Is God not the most decisive of judges?", b: "আল্লাহ কি বিচারকদের মধ্যে শ্রেষ্টতম বিচারক নন?"},
"96:1" : {s: 96, a: 1, e: "Read! In the name of your Lord who created:", b: "পাঠ করুন আপনার পালনকর্তার নামে যিনি সৃষ্টি করেছেন"},
"96:2" : {s: 96, a: 2, e: "He created manfrom a clinging form.", b: "সৃষ্টি করেছেন মানুষকে জমাট রক্ত থেকে।"},
"96:3" : {s: 96, a: 3, e: "Read! Your Lord is the Most Bountiful One", b: "পাঠ করুন, আপনার পালনকর্তা মহা দয়ালু,"},
"96:4" : {s: 96, a: 4, e: "who taught by [means of] the pen,", b: "যিনি কলমের সাহায্যে শিক্ষা দিয়েছেন,"},
"96:5" : {s: 96, a: 5, e: "who taught man what he did not know.", b: "শিক্ষা দিয়েছেন মানুষকে যা সে জানত না।"},
"96:6" : {s: 96, a: 6, e: "But man exceeds all bounds", b: "সত্যি সত্যি মানুষ সীমালংঘন করে,"},
"96:7" : {s: 96, a: 7, e: "when he thinks he is self-sufficient:", b: "এ কারণে যে, সে নিজেকে অভাবমুক্ত মনে করে।"},
"96:8" : {s: 96, a: 8, e: "[Prophet], all will return to your Lord.", b: "নিশ্চয় আপনার পালনকর্তার দিকেই প্রত্যাবর্তন হবে।"},
"96:9" : {s: 96, a: 9, e: "Have you seen the man who forbids", b: "আপনি কি তাকে দেখেছেন, যে নিষেধ করে"},
"96:10" : {s: 96, a: 10, e: "[Our] servant to pray?", b: "এক বান্দাকে যখন সে নামায পড়ে?"},
"96:11" : {s: 96, a: 11, e: "Have you seen whether he is rightly guided,", b: "আপনি কি দেখেছেন যদি সে সৎপথে থাকে।"},
"96:12" : {s: 96, a: 12, e: "or encourages true piety?", b: "অথবা খোদাভীতি শিক্ষা দেয়।"},
"96:13" : {s: 96, a: 13, e: "Have you seen whether he denies the truth and turns away from it?", b: "আপনি কি দেখেছেন, যদি সে মিথ্যারোপ করে ও মুখ ফিরিয়ে নেয়।"},
"96:14" : {s: 96, a: 14, e: "Does he not realize that God sees all?", b: "সে কি জানে না যে, আল্লাহ দেখেন?"},
"96:15" : {s: 96, a: 15, e: "No! If he does not stop, We shall drag him by his forehead&ndash;&ndash;", b: "কখনই নয়, যদি সে বিরত না হয়, তবে আমি মস্তকের সামনের কেশগুচ্ছ ধরে হেঁচড়াবই-"},
"96:16" : {s: 96, a: 16, e: "his lying, sinful forehead.", b: "মিথ্যাচারী, পাপীর কেশগুচ্ছ।"},
"96:17" : {s: 96, a: 17, e: "Let him summon his comrades;", b: "অতএব, সে তার সভাসদদেরকে আহবান করুক।"},
"96:18" : {s: 96, a: 18, e: "We shall summon the guards of Hell.", b: "আমিও আহবান করব জাহান্নামের প্রহরীদেরকে"},
"96:19" : {s: 96, a: 19, e: "No! Do not obey him [Prophet]: bow down in worship and draw close.", b: "কখনই নয়, আপনি তার আনুগত্য করবেন না। আপনি সেজদা করুন ও আমার নৈকট্য অর্জন করুন।"},
});
